try {
    var fs = new ActiveXObject("Scripting.FileSystemObject");
    var sh = WScript.CreateObject("WScript.Shell");
    var lib = eval(fs.OpenTextFile(
            sh.ExpandEnvironmentStrings("%NIH%") + "\\Lib.js", 1).ReadAll());
    lib.installerHelper = sh.ExpandEnvironmentStrings("%NIH%");

    //var before = lib.listRegistryKeys("HKEY_LOCAL_MACHINE\\SOFTWARE\\Wow6432Node\\Microsoft\\Windows\\CurrentVersion\\Uninstall");
    lib.exec(lib.findFile(".", /\.exe$/i) + " /S /D=" + sh.currentDirectory);
    //var after = lib.listRegistryKeys("HKEY_LOCAL_MACHINE\\SOFTWARE\\Wow6432Node\\Microsoft\\Windows\\CurrentVersion\\Uninstall");
    //WScript.Echo("Difference: " + before.length + " " + after.length + " " + lib.subArrays(after, before)[0]);
    var c = sh.RegRead("HKEY_LOCAL_MACHINE\\SOFTWARE\\Wow6432Node\\Microsoft\\Windows\\CurrentVersion\\Uninstall\\QCAD\\UninstallString");
    var ts = fs.CreateTextFile(".Npackd\\Uninstall.bat", true, false);
    ts.WriteLine("\"" + c + "\"");
    ts.Close();
} catch (e) {
    WScript.Echo(e.name + ": " + e.message);
    WScript.Echo(e.number + ": " + e.description);
    WScript.Quit(1);
}
